import pandas as pd## load in hedgeIndicatorhedgeIndicator_v1 = pd.read_csv('../data/hedgeIndicator.csv')hedgeIndicator_v2 = pd.read_csv('../data/hedgeIndicator.csv')## load in gvkey maps gvlist_v1 = pd.read_csv('../data/index.csv')gvlist_v2 = pd.read_csv('../data/updated_keys.csv')## merge 2 mapsgv_map = pd.concat([gvlist_v1[['gvkey','cik']], gvlist_v2[['gvkey','cik']]])gv_map = gv_map.dropna()gv_map = gv_map.drop_duplicates()gv_map.set_index(keys = gv_map['cik'], inplace=True) ##gvkeys as indicesgv_map = gv_map.drop(['cik'], axis = 1)## add in gvkey columndef gv_match(cik):    if (cik not in gv_map['cik']):        return -1    return gv_map['gvkey'].loc[cik]hedgeIndicator_v1['gvkey'] = hedgeIndicator_v1['CIK'].apply(gv_match)